sect1.sect2:
   &reftitle.examples;

    Esempio di interfaccia


<?php

// Dichiarazione dell'interfaccia 'Template'
interface Template
{
    public function setVariable($name, $var);
    public function getHtml($template);
}

// Implementazione dell'interfaccia
// Questo funzionerà
class WorkingTemplate implements Template
{
    private $vars = [];

    public function setVariable($name, $var)
    {
        $this->vars[$name] = $var;
    }

    public function getHtml($template)
    {
        foreach($this->vars as $name => $value) {
            $template = str_replace('{' . $name . '}', $value, $template);
        }

        return $template;
    }
}

// Questo non funzionerà
// Fatal error: Class BadTemplate contains 1 abstract methods
// and must therefore be declared abstract (Template::getHtml)
class BadTemplate implements Template
{
    private $vars = [];

    public function setVariable($name, $var)
    {
        $this->vars[$name] = $var;
    }
}
?>




    Interfacce estendibili


<?php
interface A
{
    public function foo();
}

interface B extends A
{
    public function baz(Baz $baz);
}

// Questo funzionerà
class C implements B
{
    public function foo()
    {
    }

    public function baz(Baz $baz)
    {
    }
}

// Questo non funzionerà e causerà un errore fatale
class D implements B
{
    public function foo()
    {
    }

    public function baz(Foo $foo)
    {
    }
}
?>




    Compatibilità della varianza con interfacce multiple


<?php
class Foo {}
class Bar extends Foo {}

interface A {
    public function myfunc(Foo $arg): Foo;
}

interface B {
    public function myfunc(Bar $arg): Bar;
}

class MyClass implements A, B
{
    public function myfunc(Foo $arg): Bar
    {
        return new Bar();
    }
}
?>




    Ereditarietà multipla delle interfacce


<?php
interface A
{
    public function foo();
}

interface B
{
    public function bar();
}

interface C extends A, B
{
    public function baz();
}

class D implements C
{
    public function foo()
    {
    }

    public function bar()
    {
    }

    public function baz()
    {
    }
}
?>




    Interfacce con costanti


<?php
interface A
{
    const B = 'Interface constant';
}

// Stampa: Interface constant
echo A::B;


class B implements A
{
    const B = 'Class constant';
}

// Stampa: Class constant
// Prima di PHP 8.1.0, questo non funzionava perché non era
// permesso sovrascrivere le costanti.
echo B::B;
?>




    Interfacce con classi astratte


<?php
interface A
{
    public function foo(string $s): string;

    public function bar(int $i): int;
}

// Una classe astratta può implementare solo una parte di un'interfaccia.
// Le classi che estendono la classe astratta devono implementare il resto.
abstract class B implements A
{
    public function foo(string $s): string
    {
        return $s . PHP_EOL;
    }
}

class C extends B
{
    public function bar(int $i): int
    {
        return $i * 2;
    }
}
?>




    Estensione e implementazione simultanee


<?php

class One
{
    /* ... */
}

interface Usable
{
    /* ... */
}

interface Updatable
{
    /* ... */
}

// L'ordine delle parole chiave qui è importante. 'extends' deve venire prima.
class Two extends One implements Usable, Updatable
{
    /* ... */
}
?>





     Un'interfaccia, insieme alle dichiarazioni di tipo, fornisce un buon modo per assicurarsi
     che un particolare oggetto contenga particolari metodi. Si vedano
     l'operatore instanceof e le
     dichiarazioni di tipo.
